    private void jButtonXoaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonXoaActionPerformed
         int selectedRow = jTableBangSach.getSelectedRow();

        if (selectedRow == -1) { // Nếu không có hàng nào được chọn
            JOptionPane.showMessageDialog(null, "Vui lòng chọn một hàng để xóa!");
            return;
        }

         // Lấy dữ liệu của hàng được chọn
        String maSach = (String) jTableBangSach.getValueAt(selectedRow, 0); // Cột MASACH
        String tuaSach = (String) jTableBangSach.getValueAt(selectedRow, 1); // Cột TUASACH
        String tacGia = (String) jTableBangSach.getValueAt(selectedRow, 2); // Cột TACGIA
        String nhaXuatBan = (String) jTableBangSach.getValueAt(selectedRow, 3); // Cột NHAXUATBAN

        // Hiển thị hộp thoại xác nhận
        int confirm = JOptionPane.showConfirmDialog(
            null,
            "Bạn có chắc chắn muốn xóa sách này?\n" +
            "- Mã sách: " + maSach + "\n" +
            "- Tựa sách: " + tuaSach + "\n" +
            "- Tác giả: " + tacGia + "\n" +
            "- Nhà xuất bản: " + nhaXuatBan,
            "Xác nhận xóa",
            JOptionPane.YES_NO_OPTION
        );

        if (confirm == JOptionPane.YES_OPTION) {
            // Thực hiện xóa sách trong cơ sở dữ liệu
            Connection ketNoi = KetNoiCSDL.layKetNoi();
            String sql = "DELETE FROM SACH WHERE MASACH = ?";
            try {
                PreparedStatement ps = ketNoi.prepareStatement(sql);
                ps.setString(1, maSach); // Gán giá trị MASACH vào câu lệnh DELETE
                int rowsAffected = ps.executeUpdate(); // Thực thi lệnh DELETE

                if (rowsAffected > 0) {
                    JOptionPane.showMessageDialog(null, "Xóa thành công!");

                    // Xóa hàng khỏi giao diện
                    DefaultTableModel model = (DefaultTableModel) jTableBangSach.getModel();
                    model.removeRow(selectedRow); // Xóa hàng được chọn khỏi bảng
                } else {
                    JOptionPane.showMessageDialog(null, "Không tìm thấy sách để xóa!");
                }

                ps.close();
                ketNoi.close();
            } catch (SQLException ex) {
                ex.printStackTrace();
                JOptionPane.showMessageDialog(null, "Lỗi khi xóa sách: " + ex.getMessage());
            }
        }
    }//GEN-LAST:event_jButtonXoaActionPerformed

