    private void jButtonThemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonThemActionPerformed
        String maSach = jTextFieldMaSach.getText().trim();
        String tuaSach = jTextFieldTuaSach.getText().trim();
        String tacGia = jTextFieldTacGia.getText().trim();
        String NXB = jTextFieldNXB.getText().trim();
        
        if(maSach.isEmpty() || tuaSach.isEmpty()  || tacGia.isEmpty()  || NXB.isEmpty()){
            JOptionPane.showMessageDialog(this, "Vui long dien day du thong tin!" , "Yêu cầu", JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        Connection ketNoi = KetNoiCSDL.layKetNoi();
        String ktMa = "select count(*) from SACH where MASACH = ?";
        String luuSach = "insert into SACH (MASACH, TUASACH, TACGIA, NHAXUATBAN) values (?, ?, ?, ?)";
        
        try{
        PreparedStatement kt = ketNoi.prepareStatement(ktMa);
        kt.setString(1, maSach);
        ResultSet rs = kt.executeQuery();
        
        if(rs.next() && rs.getInt(1) > 0){
            JOptionPane.showMessageDialog(this, "Ma sach da ton tai! Vui long dien ma sach khac!", "Lỗi", JOptionPane.ERROR_MESSAGE);
            kt.close();
            rs.close();
            return;
        }
        
       PreparedStatement luu = ketNoi.prepareStatement(luuSach);
       luu.setString(1, maSach);
       luu.setString(2, tuaSach);
       luu.setString(3, tacGia);
       luu.setString(4, NXB);
       
       int thongBao = luu.executeUpdate();
       
       if(thongBao > 0){
           JOptionPane.showMessageDialog(this, "Thêm sách thành công!", "Thông báo", JOptionPane.INFORMATION_MESSAGE);
           layThongTinSach();
          
       }
       else{
           JOptionPane.showMessageDialog(this, "Thêm sách thất bại!", "Lỗi", JOptionPane.ERROR_MESSAGE);
           layThongTinSach();
          
       }
           
       
       luu.close();
       ketNoi.close();
        
        
        
        }catch(SQLException e){
            System.out.println("Loi khong ket noi SQL");
        }
        
        
        
    }//GEN-LAST:event_jButtonThemActionPerformed

    private void jButtonSuaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSuaActionPerformed

        int selectedRow = jTableBangSach.getSelectedRow();

        if (selectedRow == -1) { // Nếu không có hàng nào được chọn
            JOptionPane.showMessageDialog(null, "Vui lòng chọn một hàng để sửa!");
            return;
        }

        // Lấy dữ liệu hiện tại của hàng được chọn
        String maSach = (String) jTableBangSach.getValueAt(selectedRow, 0); // Cột MASACH
        String tuaSach = (String) jTableBangSach.getValueAt(selectedRow, 1); // Cột TUASACH
        String tacGia = (String) jTableBangSach.getValueAt(selectedRow, 2); // Cột TACGIA
        String nhaXuatBan = (String) jTableBangSach.getValueAt(selectedRow, 3); // Cột NHAXUATBAN

        // Hiển thị hộp thoại để nhập dữ liệu mới
        String suaTuaSach = JOptionPane.showInputDialog(null, "Nhập tựa sách mới:", tuaSach);
        if (suaTuaSach == null || suaTuaSach.trim().isEmpty()) return;

        String suaTacGia = JOptionPane.showInputDialog(null, "Nhập tác giả mới:", tacGia);
        if (suaTacGia == null || suaTacGia.trim().isEmpty()) return;

        String suaNhaXuatBan = JOptionPane.showInputDialog(null, "Nhập nhà xuất bản mới:", nhaXuatBan);
        if (suaNhaXuatBan == null || suaNhaXuatBan.trim().isEmpty()) return;

        // Cập nhật thông tin trong cơ sở dữ liệu
        Connection ketNoi = KetNoiCSDL.layKetNoi();
        String sql = "UPDATE SACH SET TUASACH = ?, TACGIA = ?, NHAXUATBAN = ? WHERE MASACH = ?";
        try {
            PreparedStatement ps = ketNoi.prepareStatement(sql);
            ps.setString(1, suaTuaSach); // Giá trị mới cho cột TUASACH
            ps.setString(2, suaTacGia); // Giá trị mới cho cột TACGIA
            ps.setString(3, suaNhaXuatBan); // Giá trị mới cho cột NHAXUATBAN
            ps.setString(4, maSach); // Điều kiện WHERE với MASACH
            int rowsAffected = ps.executeUpdate();

            if (rowsAffected > 0) {
                JOptionPane.showMessageDialog(null, "Sửa thành công!");

                // Cập nhật giao diện bảng
                DefaultTableModel model = (DefaultTableModel) jTableBangSach.getModel();
                model.setValueAt(suaTuaSach, selectedRow, 1); // Cột TUASACH
                model.setValueAt(suaTacGia, selectedRow, 2); // Cột TACGIA
                model.setValueAt(suaNhaXuatBan, selectedRow, 3); // Cột NHAXUATBAN
            } else {
                JOptionPane.showMessageDialog(null, "Không tìm thấy sách để sửa!");
            }

            ps.close();
            ketNoi.close();
        } catch (SQLException ex) {
            ex.printStackTrace();
            JOptionPane.showMessageDialog(null, "Lỗi khi sửa sách: " + ex.getMessage());
        }
    
        
        
    }//GEN-LAST:event_jButtonSuaActionPerformed

