    public void layThongTinSach(){
        DefaultTableModel bang = (DefaultTableModel) jTableBangSach.getModel();
        bang.setRowCount(0);
        
        Connection ketNoi = KetNoiCSDL.layKetNoi();
        String laySach = "select * from SACH";
        
        Vector vt;
        try{
            Statement luuSach = ketNoi.createStatement();
            ResultSet rs = luuSach.executeQuery(laySach);
            
            while(rs.next()){
                vt = new Vector();
                vt.add(rs.getString("MASACH"));
                vt.add(rs.getString("TUASACH"));
                vt.add(rs.getString("TACGIA"));
                vt.add(rs.getString("NHAXUATBAN"));
                bang.addRow(vt);
            }
            jTableBangSach.setModel(bang);
          System.out.println("Lay thong tin sach thanh cong!");
          rs.close();
          luuSach.close();
          ketNoi.close();
     
        } catch (SQLException e){
            System.out.println("Loi ket noi SQL");
        }
        
    }
